<?phpdefined('_JEXEC') or die( 'Restricted access' );jimport('joomla.application.component.controller');global $mainframe;$mainframe->addCustomHeadTag( '<link rel="stylesheet" type="text/css"  href="components/com_joomsport/css/joomsport.css" />' );//template chooser$mainframe->addCustomHeadTag( '<link rel="stylesheet" type="text/css"  href="components/com_joomsport/templates/default.css" />' );require_once('includes/func.php');class BLeagueController extends JController{	/**	 * Method to display a view	 *	 * @access	public	 * @since	1.5	 */	function display()	{		$view = JRequest::getCmd( 'view' );		if(!$view) {			$view = 'ltable';		}		JRequest::setVar( 'view', $view );		parent::display();	}		function team()	{		JRequest::setVar( 'view', 'blteam' );		parent::display();	}	function view_match()	{		JRequest::setVar( 'view', 'match' );		parent::display();	}	function player()	{		JRequest::setVar( 'view', 'player' );		parent::display();	}	function calendar()	{		JRequest::setVar( 'view', 'ltable' );				JRequest::setVar( 'calendar', 1 );		parent::display();	}}	